+ String match = matchComboBox.getSelectionModel().getSelectedItem();
+ LocalDate date = datePicker.getValue();
+ boolean scoreboard = viewModeGroup.getSelectedToggle() == scoreboardRadio;
+ if (match == null || date == null) {
+ eventsTable.getItems().setAll(eventData);
+ return;
+ }
+ if (scoreboard) {
+ if (match.contains("Team X")) {
+ eventData.add(new MatchEvent("12'", "Goal", "Player X1", "Team X"));
+ eventData.add(new MatchEvent("29'", "Foul", "Player Y3", "Team Y"));
+ eventData.add(new MatchEvent("45+'", "Yellow Card", "Player X4", "Team X"));
+ eventData.add(new MatchEvent("61'", "Substitution", "Player Y7", "Team Y"));
+ } else if (match.contains("Team Z")) {
+ eventData.add(new MatchEvent("07'", "Goal", "Player Z9", "Team Z"));
+ eventData.add(new MatchEvent("38'", "Foul", "Player W2", "Team W"));
+ eventData.add(new MatchEvent("73'", "Goal", "Player W10", "Team W"));
+ } else {
+ eventData.add(new MatchEvent("20'", "Corner", "Player A5", "Team A"));
+ eventData.add(new MatchEvent("33'", "Goal", "Player B8", "Team B"));
+ }
+ } else {
+ if (match.contains("Team X")) {
+ eventData.add(new MatchEvent("Full", "Shots", "Player X1", "5"));
+ eventData.add(new MatchEvent("Full", "Pass Accuracy", "Player X6", "89%"));
+ eventData.add(new MatchEvent("Full", "Distance", "Player Y4", "10.2km"));
+ } else if (match.contains("Team Z")) {
+ eventData.add(new MatchEvent("Full", "Shots", "Player Z9", "6"));
+ eventData.add(new MatchEvent("Full", "Saves", "Player W1", "4"));
+ } else {
+ eventData.add(new MatchEvent("Full", "Assists", "Player A7", "2"));
+ eventData.add(new MatchEvent("Full", "Tackles", "Player B3", "7"));
+ }
+ }
+ eventsTable.getItems().setAll(eventData);
+ }
+
+ private void applyFilters() {
+ String q = searchField.getText() == null ? "" : searchField.getText().toLowerCase(Locale.ROOT).trim();
+ if (q.isEmpty()) {
+ eventsTable.getItems().setAll(eventData);
+ return;
+ }
+ ArrayList<MatchEvent> filtered = eventData.stream()
+ .filter(ev ->
+ (ev.getEventType() != null && ev.getEventType().toLowerCase(Locale.ROOT).contains(q)) ||
+ (ev.getPlayerName() != null && ev.getPlayerName().toLowerCase(Locale.ROOT).contains(q)) ||
+ (ev.getTeam() != null && ev.getTeam().toLowerCase(Locale.ROOT).contains(q)) ||
+ (ev.getTime() != null && ev.getTime().toLowerCase(Locale.ROOT).contains(q))
+ )
+ .collect(Collectors.toCollection(ArrayList::new));
+ eventsTable.getItems().setAll(filtered);
+ }
+
+ private void showAlert(Alert.AlertType type, String message) {
+ Alert alert = new Alert(type);
+ alert.setTitle("Live Match Updates");
+ alert.setHeaderText(null);
+ alert.setContentText(message);
+ alert.showAndWait();
+ }
+}
